import React, { Component } from 'react';

import './App.css';

import PressKey from './components/PressKey';
import DisplayScreen from './components/DisplayScreen';

class App extends Component {
  constructor() {
    super();
    this.state = {
      result: '0'
    }
  }

  changeResult = keyValue => {
    this.setState({
      result: keyValue+''
    })
  }


  render() {
    const displayLetters = [
      '(', ')', '%', 'AC', '7', '8', '9', '/', '4', '5', '6', '*', '1', '2', '3', '-', '0', '.', '=', '+'
    ]

    const pressKeyItems = displayLetters.map((displayLetter) =>
      <PressKey keyValue={displayLetter.toString()} value={displayLetter} onClick = {e => this.changeResult(e.target.value)}>
      </PressKey>
    );

    return (
      <div>
        <DisplayScreen value={this.state.result}></DisplayScreen>
        {pressKeyItems}
      </div>
    )
  }
}


export default App;
