import React, { Component } from 'react';

import './App.css';

import PressKey from './components/PressKey';
import DisplayScreen from './components/DisplayScreen';

class App extends Component {
  constructor() {
    super();
    this.state = {
      result: ['0']
    }
  }



  changeResult = keyValue => {
    const notDuplicate = [
      '%', '/', '*', '-', '.', '=', '+'
    ];
    let tempResult = [];
    let compareResult = [...this.state.result];

    let length = compareResult.length;
    let lastElement = compareResult[length - 1]
    console.log(compareResult, lastElement);
    console.log(lastElement === keyValue)
    if (keyValue === 'AC') {
      tempResult = ['0'];
    }
    else if (notDuplicate.includes(keyValue) && (compareResult.includes(keyValue) || (lastElement === keyValue))) {
      tempResult = [...this.state.result];
    }
    else {
      tempResult = [...this.state.result, keyValue];

    }
    this.setState({
      result: tempResult
    })

  }


  render() {
    const displayLetters = [
      '(', ')', '%', 'AC', '7', '8', '9', '/', '4', '5', '6', '*', '1', '2', '3', '-', '0', '.', '=', '+'
    ]

    const pressKeyItems = displayLetters.map((displayLetter) =>
      <PressKey className="press-key" key={displayLetter.toString()} value={displayLetter} onClick={e => this.changeResult(e.target.value)}>
      </PressKey>
    );

    return (
      <div className="main-wrapper">
        <DisplayScreen value={this.state.result}></DisplayScreen>
        {pressKeyItems}
      </div>
    )
  }
}


export default App;
